mapper.select:
select

from tb_admin_user
where login_user_name = #{userName,jdbcType=VARCHAR} AND login_password=#{password,jdbcType=VARCHAR}

mapper.update:
update tb_admin_user
set login_user_name = #{loginUserName,jdbcType=VARCHAR},
  login_password = #{loginPassword,jdbcType=VARCHAR},
  nick_name = #{nickName,jdbcType=VARCHAR},
  locked = #{locked,jdbcType=TINYINT}
where admin_user_id = #{adminUserId,jdbcType=INTEGER}

mapper.select:
select

from tb_admin_user


    login_user_name = #{loginUserName,jdbcType=VARCHAR}


    and login_password = #{loginPassword,jdbcType=VARCHAR}


    and nick_name = #{nickName,jdbcType=VARCHAR}


    and locked = #{locked,jdbcType=TINYINT}
